using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class switch_craft_charactor : MonoBehaviour{    public GameObject craft_inv;    public GameObject charactor_inv;    public Text craft_text;    public Text charactor_text;        // Start is called before the first frame update    public void switch_to_craft()    {        //craft_inv.SetActive(true);        charactor_inv.SetActive(false);        Color color = charactor_text.color;        color.a = 1;        craft_text.color = color;        color.a = 0.5f;        charactor_text.color = color;    }    public void switch_to_charactor()    {        //craft_inv.SetActive(false);        charactor_inv.SetActive(true);        Color color = craft_text.color;        color.a = 1;        charactor_text.color = color;        color.a = 0.5f;        craft_text.color = color;    }    void Start()    {                switch_to_craft();    }    // Update is called once per frame    void Update()    {            }}